function salvaDados(dados) {
  localStorage.setItem('db', JSON.stringify(dados));
}

function salvaDados_c(dados) {
  let strDados = localStorage.getItem('db');
  salvaDados = JSON.parse(strDados)
  salvaDados.push(dados)
  strDados = JSON.stringify(salvaDados);
  localStorage.setItem('db', strDados);
}

function veremail(x){
  let strEmail = document.getElementById('Criaremail').value
  a = 1
  for(y of x){
    if(y.email == strEmail){
      return a = 0
    }
  }
  return a
}

function incluirContato() {
  let objDados = leDados();
  maior = objDados[0].id
  for (x of objDados) {
    if (x.id > maior) {
      maior = x.id
    }
  }
  let dados = veremail(objDados)
  if(dados == 1){
    strId = maior + 1
    let strUser = document.getElementById('nomeUser').value
    let strEmail = document.getElementById('Criaremail').value
    let strSenha = document.getElementById('CriarPassword').value
  
    let novoCadastro = {
      id: strId,
      nome_user: strUser,
      email: strEmail,
      senha: strSenha,
      petBase: [],
      vacinat:[],
      vacinatp:[],
      events:[]
  
    };
    objDados.push(novoCadastro);
    salvaDados(objDados);
    tela_itens();  
  }else{
    alert("Email ja utilizado")
    document.getElementById('Criaremail').value = ""
  }
  

}

function veremail() {
  let a = 1
  let dado = leDados()
  let email_c = document.getElementById('Criaremail').value
  for (x of dado) {
    if (x.email == email_c) {
      return a = 0
    }
  }
  return a
}

function criar() {
  let dado = leDados()
  let nome_user = document.getElementById('nomeUser').value
  let email_c = document.getElementById('Criaremail').value
  let senha_c = document.getElementById('CriarPassword').value
  let cod = dado.length + 1
  if (nome_user != "" && email_c != "" && senha_c != "") {
    if (veremail() == 1) {
      let cadastro = {
        id: cod,
        nome_user: nome_user,
        email: email_c,
        senha: senha_c,
        petBase: [],
        vacinat:[],
        vacinatp:[],
        events:[]
      }
      salvaDados_c(cadastro);
    } else {
      alert("Email ja existente")
      document.getElementById('Criaremail').value = ""
    }
  }
}

function login() {
  let dados = leDados()
  let email_entrar = document.getElementById('email').value
  let senha_entrar = document.getElementById('password').value
  let cod = 0;
  for (x of dados) {
    if (x.email == email_entrar && x.senha == senha_entrar) {
      cod = x.id;
    }
  }
  if (cod > 0) {
    document.querySelector('.btn').addEventListener('click', function () {
      setTimeout(function () {
        window.location.href = "home.html?id=" + cod;
      }, 500);
    });

  } else {
    alert("Email ou senha invalida")
  }
}
function alterar() {
  let objDados = leDados();
  let emailt = document.getElementById('email_t').value;
  let novaSenha = document.getElementById('senha_nova').value;
  let confirmaSenha = document.getElementById('senha_nova_confirmação').value;
  if (novaSenha == confirmaSenha) {
    for(x of objDados){
      if(x.email == emailt){
        x.senha = novaSenha
      }
    }
  salvaDados(objDados)
  alert("Senha salva com sucesso")  
  document.getElementById('email_t').value = "";
  document.getElementById('senha_nova').value = "";
  document.getElementById('senha_nova_confirmação').value = "";
  tela_itens()
  }else {
    alert('As senhas não coincidem!');
  }
}
